import React from "react";

import "./Modal.css";

const modal = props => {
  const cssClasses = [
    "Modal",
    props.show === "entering"
      ? "ModalOpen"
      : props.show === "exiting"
        ? "ModalClosed"
        : null
  ];

  return (
    <Transition
      mountOnEnter
      unmountOnExit
      in={this.state.modalIsOpen}
      timeout={300}
    >
      <div className={cssClasses.join(" ")}>
        <h1>A Modal</h1>
        <button className="Button" onClick={props.closed}>
          Dismiss
        </button>
      </div>
    </Transition>
  );
};

export default modal;
